mapper.select:
select * from
	(select rownum rno, test_mem.* from test_mem)
 where rno>(#{pageNum}-1)*#{amount} and rno<=#{pageNum}*#{amount}

mapper.update:
update test_mem set password=#{password}, mobile=#{mobile}, memLevel=#{memLevel}, 
firstName=#{firstName}, lastName=#{lastName}, country=#{country}, streetAdress=#{streetAdress}, 
detailAddress=#{detailAddress}, zipcode=#{zipcode}, auth=#{auth} where username=#{username}

mapper.select:
select * from
	(select /*+INDEX_DESC(board pk_board) */ rownum rno,  board.* from board where writer=#{username})
 where rno>(#{memberCri.pageNum}-1)*#{memberCri.amount} and rno<=#{memberCri.pageNum}*#{memberCri.amount}

select mem.userid, userpw, username, enabled, regdate, updatedate, auth
from spring_member mem left outer join spring_member_auth auth on mem.userid = auth.userid
where mem.userid = #{userid}
