mapper.select:
select * from (
	select rownum rno, product.* 
	from product 
)
where rno>(#{pageNum}-1)*#{amount} and rno<=#{pageNum}*#{amount}

select * from ( 
	select rownum rno,product.* 
	from product 
	where 

			p_category1 = #{category1}


			p_category1 = #{category1} and p_category2 = #{category2}


			p_category1 = #{category1} and p_category2 = #{category2} and p_category3 = #{category3}

		and 
		p_code in (
			select p_code 
			from searchList 
			where 

					p_name like upper('%'||#{keyList}||'%') or 
					p_code like '%'||#{keyList}||'%' or 
					p_content like upper('%'||#{keyList}||'%') or 
					p_category1 like upper('%'||#{keyList}||'%') or 
					p_category2 like upper('%'||#{keyList}||'%') or 
					p_category3 like upper('%'||#{keyList}||'%') 

		)
) where rno>(#{pageNum}-1)*#{amount} and rno<=#{pageNum}*#{amount}

select count(*) from ( select product.* from product where
p_category1 = #{category1}
p_category1 = #{category1} and p_category2 = #{category2}
p_category1 = #{category1} and p_category2 = #{category2} and p_category3 = #{category3}
and p_code in ( select p_code from searchList where
p_name like upper('%'||#{keyList}||'%') or p_code like '%'||#{keyList}||'%' or p_content like upper('%'||#{keyList}||'%') or p_category1 like upper('%'||#{keyList}||'%') or p_category2 like upper('%'||#{keyList}||'%') or p_category3 like upper('%'||#{keyList}||'%')
) )

select * from ( 
	select rownum rno,product.* 
	from product 
	where 

			p_category1 = #{category1}


			p_category1 = #{category1} and p_category2 = #{category2}


			p_category1 = #{category1} and p_category2 = #{category2} and p_category3 = #{category3}

) where rno>(#{pageNum}-1)*#{amount} and rno<=#{pageNum}*#{amount}

