mapper.update:
update cart 
set cart_Stock = #{cart_Stock}
where cartNum = #{cartNum}

update product 
set p_rating = (select sum(p_rating) from review where p_code = #{p_code})/(select count(*) from review where p_code = #{p_code})
where p_code = #{p_code}
