mapper.select:
select * 
from product 
where

		p_category1 = #{category1}


		p_category1 = #{category1} and p_category2 = #{category2}


		p_category1 = #{category1} and p_category2 = #{category2} and p_category3 = #{category3}

select * from product where p_code in (select p_code from searchList where
p_name like upper('%'||#{keyList}||'%') or p_code like '%'||#{keyList}||'%' or p_content like upper('%'||#{keyList}||'%') or p_category1 like upper('%'||#{keyList}||'%') or p_category2 like upper('%'||#{keyList}||'%') or p_category3 like upper('%'||#{keyList}||'%')
)
